import base64, pathlib

icons = {
  16:  "",
  32:  "",
  48:  "",
  128: ""
}

path = pathlib.Path("icons")
path.mkdir(exist_ok=True)

for size, b64 in icons.items():
  # Fix padding if needed
  b64 = b64.rstrip('=')
  remainder = len(b64) % 4
  if remainder == 1:
    # Remove last character if length is 1 mod 4 (invalid)
    b64 = b64[:-1]
    remainder = len(b64) % 4
  if remainder:
    # Add padding to make length a multiple of 4
    b64 += '=' * (4 - remainder)
  (path / f"icon{size}.png").write_bytes(base64.b64decode(b64))

print("icons/ готово")